const ws = require('ws');

class WebSocket {
    /**
     * @param {String} path
     */
    constructor (path) {
        this.path = path;
    }

    /**
     * @param {Json}
     */
    init (options) {
        this.options = options;
        return this.options;
    }

    start () {
        if (typeof this.options == 'undefined') {
            return undefined;
        }

        try {
            var webSocket = new ws.Server(this.options);
        } catch {
            return undefined;
        }
        return webSocket;
    }
}

exports.WebSocket = WebSocket;