const fs   = require('fs');

const path = __dirname;

const main_file = `${path}/common/JavaScript/required/index.min.js`;

if (!fs.existsSync(main_file)) {
    throw new Error(`Le fichier "${main_file}" n'a pas été trouvé`);
}

const { setup } = require(main_file);

const logs_file = `${path}/common/JavaScript/required/logs.min.js`;

if (!fs.existsSync(logs_file)) {
    throw new Error(`Le fichier "${logs_file}" n'a pas été trouvé`);
}

const { logs } = require(logs_file);

let Logs = new logs(path);


var Main = new setup(path, Logs);
Main.init(8081);